package org.hyperledger.besu.ethereum.vm.operations;

import static org.assertj.core.api.Assertions.fail;
import static org.hyperledger.besu.ethereum.core.InMemoryKeyValueStorageProvider.createInMemoryWorldStateArchive;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.Mockito.mock;
import static org.mockito.Mockito.times;

import org.hyperledger.besu.ethereum.chain.Blockchain;
import org.hyperledger.besu.ethereum.core.Address;
import org.hyperledger.besu.ethereum.core.BlockHeader;
import org.hyperledger.besu.ethereum.core.Gas;
import org.hyperledger.besu.ethereum.core.Wei;
import org.hyperledger.besu.ethereum.core.WorldUpdater;
import org.hyperledger.besu.ethereum.debug.TraceOptions;
import org.hyperledger.besu.ethereum.mainnet.LondonGasCalculator;
import org.hyperledger.besu.ethereum.mainnet.MainnetEvmRegistries;
import org.hyperledger.besu.ethereum.vm.AbstractCallOperation;
import org.hyperledger.besu.ethereum.vm.BlockHashLookup;
import org.hyperledger.besu.ethereum.vm.Code;
import org.hyperledger.besu.ethereum.vm.DebugOperationTracer;
import org.hyperledger.besu.ethereum.vm.EVM;
import org.hyperledger.besu.ethereum.vm.GasCalculator;
import org.hyperledger.besu.ethereum.vm.MessageFrame;
import org.hyperledger.besu.ethereum.vm.OperationTracer;
import org.hyperledger.besu.ethereum.worldstate.WorldStateArchive;

import java.math.BigInteger;
import java.util.ArrayDeque;
import java.util.function.Consumer;

import org.apache.tuweni.bytes.Bytes;
import org.junit.Before;
import org.junit.Test;
import org.mockito.Mockito;

public class CallOperationTest {

  private final GasCalculator gasCalculator = new LondonGasCalculator();

  private Blockchain blockchain;
  private final Address caller =
      Address.fromHexString("0x000000000000000000000000000000ca1100f022");
  private final Address callee =
      Address.fromHexString("0x00000000000000000000000000000000b0b0face");
  private WorldUpdater worldStateUpdater;
  private EVM evm;
  private final OperationTracer tracer = new DebugOperationTracer(TraceOptions.DEFAULT);
  private MessageFrame.Builder frameBuilder;
  private CallOperation callOperation;
  private AbstractCallOperation.CodeLoader cacheSpy;

  @Before
  public void init() {
    blockchain = mock(Blockchain.class);

    final WorldStateArchive worldStateArchive = createInMemoryWorldStateArchive();

    worldStateUpdater = worldStateArchive.getMutable().updater();
    worldStateUpdater.getOrCreate(caller).getMutable().setBalance(Wei.ONE);
    worldStateUpdater
        .getOrCreate(caller)
        .getMutable()
        .setCode(Bytes.fromHexString("0x5b6000808080808063b0b0face5af15056"));
    worldStateUpdater.commit();

    worldStateUpdater
        .getOrCreate(callee)
        .getMutable()
        .setCode(
            Bytes.fromHexString(
                ""));
    worldStateUpdater.commit();

    evm = MainnetEvmRegistries.london(this.gasCalculator, BigInteger.ONE);
    this.callOperation = Mockito.spy(new CallOperation(this.gasCalculator));
    this.cacheSpy = Mockito.spy(this.callOperation.getLoader());
    this.callOperation.setLoader(this.cacheSpy);
    evm.getOperations().put(this.callOperation, 0);

    this.frameBuilder = new MessageFrame.Builder();
  }

  private MessageFrame createCaller() {

    MessageFrame frame =
        frameBuilder
            .code(
                new Code(
                    Bytes.fromHexString(
                        "0x5b6000808080808063b0b0face5af15056"))) // same as callers contract
            .type(MessageFrame.Type.MESSAGE_CALL)
            .sender(this.caller)
            .blockchain(blockchain)
            .messageFrameStack(new ArrayDeque<MessageFrame>())
            .worldState(worldStateUpdater)
            .initialGas(Gas.of(100000))
            .address(this.caller)
            .originator(Address.ZERO)
            .contract(Address.ZERO)
            .gasPrice(Wei.ONE)
            .inputData(Bytes.EMPTY)
            .value(Wei.ZERO)
            .apparentValue(Wei.ZERO)
            .blockHeader(mock(BlockHeader.class))
            .depth(1)
            .completer(
                new Consumer<MessageFrame>() {
                  @Override
                  public void accept(final MessageFrame messageFrame) {
                    System.out.println(messageFrame);
                  }
                })
            .miningBeneficiary(Address.ZERO)
            .blockHashLookup(mock(BlockHashLookup.class))
            .contractAccountVersion(0)
            .build();

    return frame;
  }

  @Test
  public void shouldNotRecalcJumpsOnSubcall() {

    MessageFrame setupCallerContract = createCaller();
    setupCallerContract.setState(MessageFrame.State.CODE_EXECUTING);
    evm.runToHalt(setupCallerContract, tracer);
    Mockito.verify(this.callOperation).execute(any(), any());
    try {
      Mockito.verify(this.cacheSpy, times(1)).load(any());
    } catch (Exception e) {
      fail("didn't cache code right", e);
    }
  }
}
